/* eslint-disable jsx-a11y/label-has-associated-control */
import React from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
// import moment from 'moment-strftime';
import { Button, Form } from 'react-bootstrap';
import { signup } from '../../actions';

const userSchema = Yup.object().shape({
  email: Yup.string().email().required(),
  password: Yup.string().required(),
  name: Yup.string().required(),
  nameKid: Yup.string().required(),
  kidBD: Yup.date(),
});

const Signup = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(userSchema),
  });

  const dispatch = useDispatch();
  const history = useHistory();

  const handleFormSubmit = (data) => {
    dispatch(
      signup(data, () => {
        history.push('/');
      })
    );
  };

  return (
    <div
      className="h-100 p-5 text-white bg-dark"
      style={{
        position: 'absolute',
        display: 'inline-block',
        width: '100%',
        marginTop: '5%',
        float: 'left',
      }}
    >
      <Form
        style={{
          position: 'relative',
          marginLeft: '22%',
          marginTop: '1%',
          display: 'inline-block',
          width: '33%',
        }}
        onSubmit={handleSubmit(handleFormSubmit)}
      >
        <h3 className="centered">Signup</h3>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter email"
            {...register('email', { required: true })}
          />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
          {errors.email?.message}
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            {...register('password', { required: true })}
          />
          {errors.password?.message}
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="name"
            placeholder="your name"
            {...register('name', { required: true })}
          />
          {errors.name?.message}
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Your child's name</Form.Label>
          <Form.Control
            type="nameKid"
            placeholder="your child's name"
            {...register('nameKid', { required: true })}
          />
          {errors.nameKid?.message}
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>your child's birthday</Form.Label>
          <Form.Control
            type="kidBD"
            placeholder="your child's birthday"
            {...register('kidBD', { required: true })}
          />
          {errors.kidBD?.message}
        </Form.Group>

        <Button variant="primary" type="submit" className="centered-button">
          Submit
        </Button>
      </Form>
    </div>
  );
};

export default Signup;
