import FETCH_DATA from '../actions';

const INITIAL_STATE = {
  h1: 'Hello. This is sleeping app',
};

export default function (state = INITIAL_STATE, action) {
  switch (action.type) {
    case FETCH_DATA:
      return action.payload;
    default:
      return state;
  }
}
