/* eslint-disable no-unused-vars */
import axios from 'axios';
import { FETCH_DATA } from './types';

export const fetchData = (input) => {
  const inputObj = input;

  return { type: FETCH_DATA, payload: inputObj };
};

export const signout = (callback) => {
  localStorage.removeItem('token');
  callback();

  return { type: 'AUTH_USER', payload: '' };
};

export async function signin(formProps, callback) {
  try {
    const response = await axios.post(
      'http://localhost:5000/auth/signin',
      formProps
    );
    localStorage.setItem('token', response.data.token);
    callback();
    return { type: 'AUTH_USER', payload: response.data };
  } catch (error) {
    return {
      type: 'AUTH_ERROR',
      payload: error.message,
    };
  }
}

export async function signup(formProps, callback) {
  try {
    const response = await axios.post(
      'http://localhost:5000/auth/signup',
      formProps
    );
    localStorage.setItem('token', response.data.token);
    localStorage.setItem('name', response.data.name);
    callback();

    return { type: 'AUTH_USER', payload: response.data };
  } catch (error) {
    return {
      type: 'AUTH_ERROR',
      payload: error.response.data.error,
    };
  }
}

export async function fetchUser() {
  const config = {
    headers: {
      Authorization: `Bearer ${localStorage.getItem('token')}`,
    },
  };
  try {
    const response = await axios.get(
      'http://localhost:5000/auth/current_user',
      config
    );
    localStorage.setItem('token', response.data.token);
    return { type: 'AUTH_USER', payload: response.data };
  } catch (error) {
    console.log(error);
  }
}

export async function postForm(formProps, callback){
  try {
    const response = await axios.post(
      'http://localhost:5000/sleepy-post',
      formProps
    );
    localStorage.setItem('token', response.data.token);
    localStorage.setItem('name', response.data.name);
    callback();

    return { type: 'AUTH_USER', payload: response.data };
  } catch (error) {
    return {
      type: 'AUTH_ERROR',
      payload: error.response.data.error,
    };
  }

}
