/* eslint-disable jsx-a11y/label-has-associated-control */
import React from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import styled from 'styled-components';
import moment from 'moment-strftime';
import { signup } from '../../actions';

const userSchema = Yup.object().shape({
  email: Yup.string().email().required(),
  password: Yup.string().required(),
  name: Yup.string().required(),
  nameKid: Yup.string().required(),
  kidBD: Yup.date()
    .transform((value) => (value ? moment(value).toDate() : value))
    .required('Date of Birth is required'),
  // .max(dateToday, 'No, it cannot be correct date...Are you from future?'),
});

const Signup = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(userSchema),
  });

  const dispatch = useDispatch();
  const history = useHistory();

  const handleFormSubmit = (data) => {
    dispatch(
      signup(data, () => {
        history.push('/');
      })
    );
  };

  return (



    <SignUpStyles>
      SignUp
      <form onSubmit={handleSubmit(handleFormSubmit)}>
        <div className="form-group">
          <label>Email</label>
          <input
            className="form-control"
            // name="email"
            // ref={register({ required: true })}
            {...register('email', { required: true })}
          />
          {errors.email?.message}
        </div>

        <div className="form-group">
          <label>Password</label>
          <input
            className="form-control"
            // name="password"
            // ref={register({ required: true })}
            {...register('password', { required: true })}
          />
          {errors.password?.message}
        </div>

        <div className="form-group">
          <label>Your name</label>
          <input
            className="form-control"
            // name="name"
            // ref={register({ required: true })}
            {...register('name', { required: true })}
          />
          {errors.name?.message}
        </div>

        <div className="form-group">
          <label>Your kid's name</label>
          <input
            className="form-control"
            // name="nameKid"
            // ref={register({ required: true })}
            {...register('nameKid', { required: true })}
          />
          {errors.nameKid?.message}
        </div>

        <div className="form-group">
          <label>Your kid's birthday</label>
          <input
            className="form-control"
            // name="kidBD"
            // ref={register({ required: true })}
            {...register('kidBD', { required: true })}
          />
          {errors.kidBD?.message}
        </div>

        <button className="btn btn-primary" type="submit">
          Submit
        </button>
      </form>
    </SignUpStyles>
  );
};

export default Signup;

const SignUpStyles = styled.div`
  margin-top: 40px;
`;
