import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Fragment } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ReactDOM from 'react-dom/client';
import { Provider } from 'react-redux';
import { applyMiddleware } from 'redux';
import { configureStore } from '@reduxjs/toolkit';
import promise from 'redux-promise';
// import thunk from 'redux-thunk';

import rootReducer from './reducers/index';
import Nav from './components/Nav';
import App from './components/App';
import Main from './components/Main';
import Tab from './components/Tab';
import Personal from './components/Personal';
import Signup from './components/auth/Signup';
import Signin from './components/auth/Signin';

// const store = configureStore(rootReducer, {}, applyMiddleware(thunk));

const createStoreWithMiddleware = applyMiddleware(promise)(configureStore);
const root = ReactDOM.createRoot(document.getElementById('root'));

root.render(
  <Provider store={createStoreWithMiddleware({ reducer: rootReducer })}>
    <Router>
      <>
        <Nav />
        <Tab />
        <App>
          <Switch>
            <Route exact path="/" component={Main} />
            <Route exact path="/signup" component={Signup} />
            <Route exact path="/signin" component={Signin} />
            <Route exact path="/personal" component={Personal} />
            {/* 
            <Route exact path="/watch-list">
              <Personal type="watch-list" />
            </Route> */}

            {/* <Route exact path="/watch-list/:id" component={MovieDetail} /> */}

            {/* <Route exact path="/:id">
              <MovieDetail>
                <WatchListButton />
              </MovieDetail> */}
            {/* </Route> */}
          </Switch>
        </App>
      </>
    </Router>
  </Provider>
);
