/* eslint-disable jsx-a11y/label-has-associated-control */
import React from 'react';
import { useForm } from 'react-hook-form';
import { useSelector, useDispatch } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
// import moment from 'moment-strftime';
import { Button, Form } from 'react-bootstrap';
import { signup } from '../../actions';

const userSchema = Yup.object().shape({
  email: Yup.string().email().required(),
  password: Yup.string().required(),
  name: Yup.string().required(),
  nameKid: Yup.string().required(),
  kidBD: Yup.date().nullable().required()
});

const Signup = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(userSchema),
  });

  const dispatch = useDispatch();
  const history = useHistory();
  const error = useSelector((state) => state.auth.errorMessage);

  const handleFormSubmit = (data) => {
    dispatch(
      signup(data, () => {
        history.push('/');
      })
    );
  };

  const errorRender = () => {
    if (error) {
      return <p className="error-message">{error}</p>;
    }
  };

  return (
    <div
      className="h-auto p-5 text-white bg-dark"
      style={{
        position: 'absolute',
        display: 'inline-block',
        width: '100%',
        marginTop: '5%',
        float: 'left',
      }}
    >
      <Form
        style={{
          position: 'relative',
          marginLeft: '22%',
          marginTop: '1%',
          display: 'inline-block',
          width: '33%',
        }}
        onSubmit={handleSubmit(handleFormSubmit)}
      >
        <h3 className="centered">Signup</h3>
        {errorRender()}
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            placeholder="enter email"
            {...register('email', { required: true })}
          />
          {errors.email && (
            <p className="error-message">Email is a required field</p>
          )}
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="password"
            {...register('password', { required: true })}
          />
          {errors.password && (
            <p className="error-message">Password is a required field</p>
          )}
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="name"
            placeholder="your name"
            {...register('name', { required: true })}
          />
          {errors.name && (
            <p className="error-message">Name is a required field</p>
          )}
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Your child's name</Form.Label>
          <Form.Control
            type="nameKid"
            placeholder="kid's name"
            {...register('nameKid', { required: true })}
          />
          {errors.nameKid && (
            <p className="error-message">Please, provide your kid's name</p>
          )}
        </Form.Group>
        <Form.Group className="mb-3" controlId="formDate">
          <Form.Label>Your kid's birthday DDMMYYYY</Form.Label>
          <Form.Control type="date" {...register('date', { required: true })} />
          {errors.kidBD && (
            <p className="error-message">Please, provide your kid's birthday</p>
          )}
        </Form.Group>

        <Button variant="primary" type="submit" className="centered-button">
          Submit
        </Button>
      </Form>
    </div>
  );
};

export default Signup;
