import { combineReducers } from '@reduxjs/toolkit';
import AuthReducer from './auth-reducer';
// import MainReducer from './main-reducer';

const rootReducer = combineReducers({
  auth: AuthReducer,
  main: MainReducer,
});
export default rootReducer;
